import { IAction } from './../models/actionModel';

export interface IReturn {
  [index: string]: IAction;
}

export function httpCreator(action: IAction): { HTTP_ACTION: IAction } {
  const httpActionTemp = {
    type: '',
    endpoint: null,
    verb: 'GET',
    payload: null,
    headers: [],
  };
  return {
    HTTP_ACTION: Object.assign({}, httpActionTemp, action),
  };
}
