export interface IAction {
  type: string;
  endpoint: string | null;
  verb?: string;
  payload?: any;
  headers?: any[];
}

export function httpCreator(action: IAction): any {
  const httpActionTemp = {
    type: '',
    endpoint: null,
    verb: 'GET',
    payload: null,
    headers: [],
  };
  return {
    HTTP_ACTION: Object.assign({}, httpActionTemp, action),
  };
}
