import { IAction } from './../models/actionModel';

interface IReturn {
  HTTP_ACTION: IAction;
}

export function httpCreator(action: IAction): IReturn {
  const httpActionTemp = {
    type: '',
    endpoint: null,
    verb: 'GET',
    payload: null,
    headers: [],
  };
  return {
    HTTP_ACTION: Object.assign({}, httpActionTemp, action),
  };
}
