import { TEST_REQUESTED, TEST_RECEIVED, TEST_FAILED } from './actionTypes';

interface IMainReducer {
  test: string;
  status: string;
}

export const InitialMainReducer: any = {
  test: '',
};
// TODO describe all anyies
export const main: (state: any, action: any) => any = (
  state: IMainReducer = InitialMainReducer,
  action: any,
) => {
  switch (action.type) {
    case TEST_REQUESTED:
      return { ...InitialMainReducer, status: 'waiting' };
    case TEST_RECEIVED:
      return { ...InitialMainReducer, data: action.payload };
    case TEST_FAILED:
      return { ...InitialMainReducer, error: action.payload, status: 'failed' };
    default:
      return InitialMainReducer;
  }
};
